// 测试localStorage中的技能数据
console.log('==================== 技能数据测试 ====================');
console.log('请打开浏览器开发者工具(按F12)查看此输出');
const savedSkills = localStorage.getItem('skills');
console.log('localStorage中的原始数据:', savedSkills);

if (savedSkills) {
  try {
    const skills = JSON.parse(savedSkills);
    console.log('解析后的数据:', skills);
    console.log('技能数量:', skills.length);
    if (skills.length > 0) {
      console.log('第一个技能:', skills[0]);
      console.log('第一个技能的练习时间:', skills[0].hoursPracticed);
    } else {
      console.log('技能数据为空数组');
      // 添加测试数据
      addTestData();
    }
  } catch (e) {
    console.error('解析数据出错:', e);
    // 重置数据
    addTestData();
  }
} else {
  console.log('localStorage中没有找到技能数据');
  // 添加测试数据
  addTestData();
}

// 添加测试数据的函数
function addTestData() {
  const testSkills = [
    {
      id: Date.now(),
      name: '编程',
      hoursPracticed: 150,
      targetHours: 10000,
      createdAt: new Date().toISOString()
    },
    {
      id: Date.now() + 1,
      name: '英语',
      hoursPracticed: 300,
      targetHours: 10000,
      createdAt: new Date().toISOString()
    }
  ];
  localStorage.setItem('skills', JSON.stringify(testSkills));
  console.log('已添加测试数据:', testSkills);
  console.log('请刷新页面查看效果');
}
console.log('======================================================');
