import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';

// 注册所有必要的组件
Chart.register(...registerables);

const ChartTest = () => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    // 销毁旧图表
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    // 创建测试数据
    const data = {
      labels: ['编程', '英语', '吉他'],
      datasets: [{
        label: '练习时间(小时)',
        data: [500, 1000, 200],
        backgroundColor: [
          'rgba(97, 218, 251, 0.5)',
          'rgba(33, 161, 241, 0.5)',
          'rgba(123, 31, 162, 0.5)'
        ],
        borderColor: [
          'rgba(97, 218, 251, 1)',
          'rgba(33, 161, 241, 1)',
          'rgba(123, 31, 162, 1)'
        ],
        borderWidth: 1
      }]
    };

    // 创建图表
    if (chartRef.current) {
      chartInstance.current = new Chart(chartRef.current, {
        type: 'bar',
        data: data,
        options: {
          responsive: true,
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });
    }

    // 组件卸载时销毁图表
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, []);

  return (
    <div style={{ width: '400px', height: '300px' }}>
      <canvas ref={chartRef}></canvas>
    </div>
  );
};

export default ChartTest;
