import React, { useContext, useEffect, useRef, useState } from 'react';
import { Chart, registerables } from 'chart.js';
import { ResponsiveContainer } from 'recharts';
import SkillContext from '../contexts/SkillContext';
import './SkillProgress.css';

// 注册所有必要的组件
Chart.register(...registerables);

const SkillProgress = () => {
  const { skills, getSkillProgress } = useContext(SkillContext);
  const pieChartRef = useRef(null);
  const barChartRef = useRef(null);
  const pieChartInstance = useRef(null);
  const barChartInstance = useRef(null);
  const [refReady, setRefReady] = useState(false);

  // 打印技能数据，用于调试
  console.log('===== 技能数据调试信息 =====');
  console.log('技能数量:', skills.length);
  console.log('技能数据:', skills);
  if (skills.length > 0) {
    console.log('第一个技能的练习时间:', skills[0].hoursPracticed);
  }
  console.log('============================');

  // 销毁旧图表实例
  const destroyCharts = () => {
    if (pieChartInstance.current) {
      pieChartInstance.current.destroy();
      pieChartInstance.current = null;
    }
    if (barChartInstance.current) {
      barChartInstance.current.destroy();
      barChartInstance.current = null;
    }
  };

  // 检测ref是否已准备好
  useEffect(() => {
    if (pieChartRef.current && barChartRef.current) {
      console.log('图表ref已准备就绪');
      setRefReady(true);
    }
  }, []);

  // 创建或更新图表
  useEffect(() => {
    // 如果ref尚未准备好，不创建图表
    if (!refReady) {
      console.log('ref尚未准备好，推迟创建图表');
      return;
    }

    // 销毁旧图表
    destroyCharts();

    if (skills.length === 0) {
      console.log('没有技能数据，不创建图表');
      return;
    }

    console.log('开始创建图表...');

    // 创建饼图数据
    const pieData = {
      labels: skills.map(skill => skill.name),
      datasets: [{
        data: skills.map(skill => skill.hoursPracticed),
        backgroundColor: [
          '#61dafb', '#21a1f1', '#7b1fa2', '#e1bee7',
          '#388e3c', '#c8e6c9', '#ff6f00', '#ffecb3'
        ],
        borderWidth: 1
      }]
    };

    console.log('饼图数据:', pieData);

    // 创建饼图
    if (pieChartRef.current) {
      try {
        pieChartInstance.current = new Chart(pieChartRef.current, {
          type: 'pie',
          data: pieData,
          options: {
            responsive: true,
            plugins: {
              legend: {
                position: 'right',
              },
              title: {
                display: true,
                text: '技能练习时间分布'
              }
            }
          }
        });
        console.log('饼图创建成功');
      } catch (error) {
        console.error('创建饼图时出错:', error);
      }
    } else {
      console.error('pieChartRef.current 为空');
    }

    // 创建柱状图数据
    const barData = {
      labels: skills.map(skill => skill.name),
      datasets: [{
        label: '已练习小时数',
        data: skills.map(skill => skill.hoursPracticed),
        backgroundColor: '#61dafb',
        borderWidth: 1
      }, {
        label: '目标小时数',
        data: skills.map(skill => skill.targetHours),
        backgroundColor: '#e0e0e0',
        borderWidth: 1,
        type: 'bar',
        order: 2
      }]
    };

    console.log('柱状图数据:', barData);

    // 创建柱状图
    if (barChartRef.current) {
      try {
        barChartInstance.current = new Chart(barChartRef.current, {
          type: 'bar',
          data: barData,
          options: {
            responsive: true,
            scales: {
              y: {
                beginAtZero: true,
                title: {
                  display: true,
                  text: '小时数'
                }
              },
              x: {
                title: {
                  display: true,
                  text: '技能'
                }
              }
            },
            plugins: {
              title: {
                display: true,
                text: '技能进度对比'
              }
            }
          }
        });
        console.log('柱状图创建成功');
      } catch (error) {
        console.error('创建柱状图时出错:', error);
      }
    } else {
      console.error('barChartRef.current 为空');
    }

    // 组件卸载时销毁图表
    return () => destroyCharts();
  }, [skills, getSkillProgress, refReady]);

  if (skills.length === 0) {
    return (
      <div className="skill-progress empty">
        <p>暂无技能数据，添加技能并记录练习时间后即可查看图表</p>
      </div>
    );
  }

  return (
    <div className="skill-progress">
      <h2>技能进度图表</h2>
      <div className="charts-container">
        <div className="chart-item">
          <ResponsiveContainer width="100%" height={300}>
            <canvas ref={pieChartRef}></canvas>
          </ResponsiveContainer>
        </div>
        <div className="chart-item">
          <ResponsiveContainer width="100%" height={300}>
            <canvas ref={barChartRef}></canvas>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default SkillProgress;
